import React, { useEffect } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const TransEffect = () => {
  useEffect(() => {
    // 모든 pre 태그 선택
    const codeBlocks = document.querySelectorAll("pre");

    // 스타일 추가 - 복사됨 메시지를 위한 CSS 클래스
    if (!document.getElementById("copy-style")) {
      const style = document.createElement("style");
      style.id = "copy-style";
      style.textContent = `
          .copied::after {
            content: "";
            position: absolute;
            top: 10px;
            right: 10px;
            width: 20px;
            height: 20px;
            background-image: url("/images/copyOk.svg");
            animation: fade-out 2s forwards;
          }
          
          @keyframes fade-out {
            0% { opacity: 1; }
            70% { opacity: 1; }
            100% { opacity: 0; }
          }
        `;
      document.head.appendChild(style);
    }

    const handleCodeBlockClick = (e) => {
      const codeBlock = e.currentTarget;
      const code = codeBlock.textContent;

      navigator.clipboard
        .writeText(code)
        .then(() => {
          // 복사 후 클래스 추가
          codeBlock.classList.add("copied");

          // 애니메이션이 끝난 후 클래스 제거
          setTimeout(() => {
            codeBlock.classList.remove("copied");
          }, 2000);
        })
        .catch((err) => console.error("복사 실패:", err));
    };

    // 이벤트 리스너 등록
    codeBlocks.forEach((block) => {
      block.removeEventListener("click", handleCodeBlockClick);
      block.addEventListener("click", handleCodeBlockClick);
      block.style.cursor = "pointer"; // 클릭 가능함을 시각적으로 표시
    });

    // 클린업 함수
    return () => {
      codeBlocks.forEach((block) => {
        block.removeEventListener("click", handleCodeBlockClick);
      });
    };
  }, []);
  return (
    <Container>
      <Header>
        <BackLink to="/css-animations">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            viewBox="0 0 24 24"
          >
            <g
              fill="none"
              stroke="#f1f1f1"
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
            >
              <path
                strokeDasharray="64"
                strokeDashoffset="64"
                d="M21 12c0 4.97 -4.03 9 -9 9c-4.97 0 -9 -4.03 -9 -9c0 -4.97 4.03 -9 9 -9c4.97 0 9 4.03 9 9Z"
              >
                <animate
                  fill="freeze"
                  attributeName="stroke-dashoffset"
                  dur="0.6s"
                  values="64;0"
                />
              </path>
              <path
                strokeDasharray="6"
                strokeDashoffset="6"
                d="M10 12l3 -3M10 12l3 3"
              >
                <animate
                  fill="freeze"
                  attributeName="stroke-dashoffset"
                  begin="0.7s"
                  dur="0.3s"
                  values="6;0"
                />
              </path>
            </g>
          </svg>
        </BackLink>
        <Title>변형 효과</Title>
        <Subtitle>
          회전, 크기 조절, 기울임 등 요소의 형태를 변경하는 다양한 변형 효과
        </Subtitle>
      </Header>

      <AnimationGrid>
        {/* 기본 변형 효과 섹션 */}
        <SectionTitle>기본 변형 효과 (Basic Transforms)</SectionTitle>

        {/* 회전 변형 */}
        <AnimationCard>
          <AnimationTitle>회전 (Rotate)</AnimationTitle>
          <RotateBox />
          <CodeBlock>
            {`transform: rotate(0deg);
transition: transform 0.3s ease;
&:hover {
  transform: rotate(45deg);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 확대/축소 변형 */}
        <AnimationCard>
          <AnimationTitle>확대/축소 (Scale)</AnimationTitle>
          <ScaleBox />
          <CodeBlock>
            {`transform: scale(1);
transition: transform 0.3s ease;
&:hover {
  transform: scale(1.2);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 이동 변형 */}
        <AnimationCard>
          <AnimationTitle>이동 (Translate)</AnimationTitle>
          <TranslateBox />
          <CodeBlock>
            {`transform: translateY(0);
transition: transform 0.3s ease;
&:hover {
  transform: translateY(-20px);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 기울임 변형 */}
        <AnimationCard>
          <AnimationTitle>기울임 (Skew)</AnimationTitle>
          <SkewBox />
          <CodeBlock>
            {`transform: skew(0deg);
transition: transform 0.3s ease;
&:hover {
  transform: skew(15deg);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 원근감 변형 */}
        <AnimationCard>
          <AnimationTitle>원근감 (Perspective)</AnimationTitle>
          <PerspectiveContainer>
            <PerspectiveBox />
          </PerspectiveContainer>
          <CodeBlock>
            {`perspective: 500px;
.box {
  transition: transform 0.3s ease;
  transform: rotateY(0deg);
  &:hover {
    transform: rotateY(45deg);
  }
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 3D 플립 효과 - 추가 */}
        <AnimationCard>
          <AnimationTitle>3D 플립 (Flip)</AnimationTitle>
          <FlipContainer>
            <FlipBox>
              <FlipFront>앞면</FlipFront>
              <FlipBack>뒷면</FlipBack>
            </FlipBox>
          </FlipContainer>
          <CodeBlock>
            {`perspective: 1000px;
.flip-box {
  position: relative;
  transform-style: preserve-3d;
  transition: transform 0.6s;
  &:hover {
    transform: rotateY(180deg);
  }
}
.front, .back {
  position: absolute;
  backface-visibility: hidden;
}
.back {
  transform: rotateY(180deg);
}`}
