  overflow: hidden;
  border-right: 2px solid #f1f5f9;
  white-space: nowrap;
  margin: 0 auto;
  width: 0;
  animation: typing 3s steps(22) 1s forwards,
    cursor 0.4s step-end infinite alternate;

  @keyframes typing {
    to {
      width: 100%;
    }
  }
  @keyframes cursor {
    50% {
      border-color: transparent;
    }
  }
