import React from "react";
import styled, { keyframes } from "styled-components";
import { Link } from "react-router-dom";

const Keyframes = () => {
  return (
    <Container>
      <Header>
        <BackLink to="/css-animations">â† ì• ë‹ˆë©”ì´ì…˜ ë©”ì¸ìœ¼ë¡œ</BackLink>
