import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const TransEffect = () => {
  return (
    <Container>
      <Header>
        <BackLink to="/css-animations">← 애니메이션 메인으로</BackLink>
        <Title>변형 효과</Title>
        <Subtitle>
          회전, 크기 조절, 기울임 등 요소의 형태를 변경하는 다양한 변형 효과
        </Subtitle>
      </Header>

      <AnimationGrid>
        {/* 기본 변형 효과 섹션 */}
        <SectionTitle>기본 변형 효과 (Basic Transforms)</SectionTitle>

        {/* 회전 변형 */}
        <AnimationCard>
          <AnimationTitle>회전 (Rotate)</AnimationTitle>
          <RotateBox />
          <CodeBlock>
            {`transform: rotate(0deg);
transition: transform 0.3s ease;
&:hover {
  transform: rotate(45deg);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 확대/축소 변형 */}
        <AnimationCard>
          <AnimationTitle>확대/축소 (Scale)</AnimationTitle>
          <ScaleBox />
          <CodeBlock>
            {`transform: scale(1);
transition: transform 0.3s ease;
&:hover {
  transform: scale(1.2);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 이동 변형 */}
        <AnimationCard>
          <AnimationTitle>이동 (Translate)</AnimationTitle>
          <TranslateBox />
          <CodeBlock>
            {`transform: translateY(0);
