import React, { useState, useEffect } from "react";
import styled, { keyframes } from "styled-components";
import { Link } from "react-router-dom";

const Animations = () => {
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  return (
    <Wrapper $isLoaded={isLoaded}>
      <GradientBackground>
        <Container>
          <Header $isLoaded={isLoaded}>
            <TitleWrapper>
              <Title>CSS Ïï†ÎãàÎ©îÏù¥ÏÖò</Title>
              <TitleAccent>.</TitleAccent>
            </TitleWrapper>
            <Subtitle>
              <span>Î™®Îçò ÏõπÏùÑ ÏúÑÌïú ÌïÑÏàò Ïï†ÎãàÎ©îÏù¥ÏÖò Í∏∞Î≤ï</span>
            </Subtitle>
          </Header>

          <CategorySection $isLoaded={isLoaded}>
            <CategoryCard to="/css-animations/trans-effect">
              <CardContent>
                <IconWrapper>
                  <CategoryIcon>
                    <AnimatedEmoji className="transform">üîÑ</AnimatedEmoji>
                  </CategoryIcon>
                </IconWrapper>
                <CategoryInfo>
                  <CategoryTitle>Ìä∏ÎûúÏßÄÏÖò & Ìä∏ÎûúÏä§Ìèº</CategoryTitle>
                  <CategoryDescription>
                    ÏöîÏÜåÏùò Î∂ÄÎìúÎü¨Ïö¥ ÏÉÅÌÉú Î≥ÄÌôîÏôÄ ÌòïÌÉú Î≥ÄÌòïÏùÑ ÌÜµÌïú Îã§ÏñëÌïú ÏãúÍ∞ÅÏ†Å
                    Ìö®Í≥º
                  </CategoryDescription>
                  <ViewButton>
                    <ButtonText>ÏûêÏÑ∏Ìûà Î≥¥Í∏∞</ButtonText>
                    <ButtonArrow>‚Üí</ButtonArrow>
                  </ViewButton>
                </CategoryInfo>
              </CardContent>
              <CardGlow />
            </CategoryCard>

            <CategoryCard to="/css-animations/keyframes">
              <CardContent>
                <IconWrapper>
                  <CategoryIcon>
                    <AnimatedEmoji className="keyframe">üé¨</AnimatedEmoji>
                  </CategoryIcon>
                </IconWrapper>
                <CategoryInfo>
                  <CategoryTitle>ÌÇ§ÌîÑÎ†àÏûÑ</CategoryTitle>
                  <CategoryDescription>
                    ÏãúÍ∞ÑÏóê Îî∞Î•∏ Îã®Í≥ÑÎ≥Ñ Ïï†ÎãàÎ©îÏù¥ÏÖòÏùÑ Ï†ïÏùòÌïòÏó¨ Ïó≠ÎèôÏ†ÅÏù∏ ÏõÄÏßÅÏûÑ
                    Íµ¨ÌòÑ
                  </CategoryDescription>
                  <ViewButton>
                    <ButtonText>ÏûêÏÑ∏Ìûà Î≥¥Í∏∞</ButtonText>
                    <ButtonArrow>‚Üí</ButtonArrow>
                  </ViewButton>
                </CategoryInfo>
              </CardContent>
              <CardGlow />
            </CategoryCard>

            <CategoryCard to="/css-animations/loading">
              <CardContent>
                <IconWrapper>
                  <CategoryIcon>
                    <AnimatedEmoji className="loading">‚è≥</AnimatedEmoji>
                  </CategoryIcon>
                </IconWrapper>
                <CategoryInfo>
                  <CategoryTitle>Î°úÎî© Ïï†ÎãàÎ©îÏù¥ÏÖò</CategoryTitle>
                  <CategoryDescription>
                    ÏÇ¨Ïö©Ïûê Í≤ΩÌóòÏùÑ Ìñ•ÏÉÅÏãúÌÇ§Îäî Îã§ÏñëÌïú Ïä§ÌÉÄÏùºÏùò Î°úÎî© Ïù∏ÎîîÏºÄÏù¥ÌÑ∞
                    Ìö®Í≥º
                  </CategoryDescription>
                  <ViewButton>
                    <ButtonText>ÏûêÏÑ∏Ìûà Î≥¥Í∏∞</ButtonText>
                    <ButtonArrow>‚Üí</ButtonArrow>
                  </ViewButton>
                </CategoryInfo>
              </CardContent>
              <CardGlow />
            </CategoryCard>
          </CategorySection>

          <FooterSection $isLoaded={isLoaded}>
            <FooterText>ÏµúÏã† CSS Í∏∞Ïà†Î°ú Í≤ΩÌóòÏùÑ Ìñ•ÏÉÅÏãúÌÇ§ÏÑ∏Ïöî</FooterText>
          </FooterSection>
        </Container>
      </GradientBackground>
    </Wrapper>
  );
};

export default Animations;

// Ïï†ÎãàÎ©îÏù¥ÏÖò Ï†ïÏùò

const glowPulse = keyframes`
  0% { opacity: 0.5; }
  50% { opacity: 0.8; }
  100% { opacity: 0.5; }
`;

const rotate = keyframes`
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.1); }
  100% { transform: scale(1); }
`;

const flip = keyframes`
  0% { transform: rotateY(0); }
  100% { transform: rotateY(360deg); }
`;

const shimmer = keyframes`
  0% { background-position: -200% 0; }
  100% { background-position: 200% 0; }
`;

// ÎØ∏ÎîîÏñ¥ ÏøºÎ¶¨Î•º ÏúÑÌïú Î∏åÎ†àÏù¥ÌÅ¨Ìè¨Ïù∏Ìä∏ Ï†ïÏùò
const breakpoints = {
  mobile: "480px",
  tablet: "768px",
  laptop: "1024px",
  desktop: "1200px",
};

// ÎØ∏ÎîîÏñ¥ ÏøºÎ¶¨ Ìó¨Ìçº Ìï®Ïàò
const media = {
  mobile: `@media (max-width: ${breakpoints.mobile})`,
  tablet: `@media (max-width: ${breakpoints.tablet})`,
  laptop: `@media (max-width: ${breakpoints.laptop})`,
  desktop: `@media (max-width: ${breakpoints.desktop})`,
};

// Ïä§ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏ Ï†ïÏùò
const Wrapper = styled.div`
  min-height: 100vh;
  opacity: ${(props) => (props.$isLoaded ? 1 : 0)};
  transition: opacity 0.8s ease-in-out;
`;

const GradientBackground = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
  position: relative;
  overflow: hidden;

  &::before {
    content: "";
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(
      circle,
      rgba(99, 102, 241, 0.15) 0%,
      rgba(99, 102, 241, 0) 60%
    );
    pointer-events: none;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 4rem 2rem;
  position: relative;
  font-family: "Inter", "Noto Sans KR", sans-serif;
  color: #f8fafc;

  ${media.tablet} {
    padding: 3rem 1.5rem;
  }

  ${media.mobile} {
    padding: 2rem 1rem;
  }
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 4rem;
  opacity: ${(props) => (props.$isLoaded ? 1 : 0)};
  transform: ${(props) =>
    props.$isLoaded ? "translateY(0)" : "translateY(30px)"};
  transition: opacity 0.8s ease, transform 0.8s ease;
  transition-delay: 0.2s;

  ${media.mobile} {
    margin-bottom: 3rem;
  }
`;

const TitleWrapper = styled.div`
  display: inline-flex;
  align-items: center;
  margin-bottom: 1rem;
`;

const Title = styled.h1`
  font-size: 3.5rem;
  font-weight: 800;
  letter-spacing: -0.03em;
  background: linear-gradient(135deg, #f9fafb 0%, #94a3b8 100%);
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;

  ${media.laptop} {
    font-size: 3rem;
  }

  ${media.tablet} {
    font-size: 2.5rem;
  }

  ${media.mobile} {
    font-size: 2rem;
  }
`;

const TitleAccent = styled.span`
  font-size: 3.5rem;
  font-weight: 800;
  color: #6366f1;
  margin-left: 2px;

  ${media.laptop} {
    font-size: 3rem;
  }

  ${media.tablet} {
    font-size: 2.5rem;
  }

  ${media.mobile} {
    font-size: 2rem;
  }
`;

const Subtitle = styled.p`
  font-size: 1.3rem;
  font-weight: 300;
  color: #cbd5e1;
  line-height: 1.5;
  max-width: 650px;
  margin: 0 auto;

  span {
    display: inline-block;
    background: linear-gradient(90deg, #cbd5e1 0%, #cbd5e1 50%, #94a3b8 100%);
    background-size: 200% auto;
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
