import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
} from "react-router-dom";
import { useEffect } from "react";
import "./App.css";
import Main from "./main/Main";
import Css from "./Css/Css";
import Animations from "./Css/Animation";

const PageTitleUpdater = () => {
  const location = useLocation();

  useEffect(() => {
    const pageTitles = {
      "/trans-effect": "트랜지션 & 트랜스폼",
      "/notice": "K-Edu 최신 소식 | 공지사항",
      "/job": "K-Edu 채용 정보 | 취업",
    };

    let title = pageTitles[location.pathname] || "Snoer Animation";

    // notice 경로에 대한 특별 처리
    if (location.pathname.startsWith("/notice/")) {
      const noticeId = location.pathname.split("/")[2]; // URL에서 ID 추출
      title = ` K-Edu 최신 소식 | ${noticeId}`;
    }

    document.title = title;
  }, [location.pathname]);

  return null;
};

function App() {
  return (
    <Router>
      <PageTitleUpdater />
      <Routes>
        <Route path="/" element={<Main />} />
        <Route path="/css-animations" element={<Animations />} />
        <Route path="/css-animations/*" element={<Css />} />
      </Routes>
    </Router>
  );
}

export default App;
