import React, { useEffect, useRef } from "react";
import styled, { keyframes } from "styled-components";
import { Link } from "react-router-dom";

const Hover = () => {
  // 마우스 추적 효과를 위한 ref와 함수 추가
  const mouseFollowBoxRef = useRef(null);
  const mouseFollowSpotRef = useRef(null);

  useEffect(() => {
    const boxElement = mouseFollowBoxRef.current;
    const spotElement = mouseFollowSpotRef.current;

    if (!boxElement || !spotElement) return;

    const handleMouseMove = (e) => {
      const rect = boxElement.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      spotElement.style.left = `${x}px`;
      spotElement.style.top = `${y}px`;
    };

    boxElement.addEventListener("mousemove", handleMouseMove);

    return () => {
      boxElement.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  return (
    <Container>
      <Header>
        <BackLink to="/css-animations">← 애니메이션 메인으로</BackLink>
        <Title>마우스 오버 인터랙션 효과</Title>
        <Subtitle>
          사용자의 마우스 오버 시 적용되는 다양한 인터랙션 효과 모음집
        </Subtitle>
      </Header>

      <AnimationGrid>
        {/* 스케일 확대 효과 */}
        <AnimationCard>
          <AnimationTitle>스케일 확대 (Scale Up)</AnimationTitle>
          <AnimationContainer>
            <ScaleUpBox>Hover Me</ScaleUpBox>
          </AnimationContainer>
          <CodeBlock>
            {`transition: transform 0.3s ease;

&:hover {
  transform: scale(1.1);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 그림자 효과 */}
        <AnimationCard>
          <AnimationTitle>그림자 효과 (Shadow)</AnimationTitle>
          <AnimationContainer>
            <ShadowBox>Hover Me</ShadowBox>
          </AnimationContainer>
          <CodeBlock>
            {`transition: box-shadow 0.3s ease;

&:hover {
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 
              0 6px 6px rgba(0, 0, 0, 0.23);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 배경색 변경 */}
        <AnimationCard>
          <AnimationTitle>배경색 변경 (Background)</AnimationTitle>
          <AnimationContainer>
            <BackgroundBox>Hover Me</BackgroundBox>
          </AnimationContainer>
          <CodeBlock>
