        {/* 글로우 효과 */}
        <AnimationCard>
          <AnimationTitle>글로우 효과 (Glow)</AnimationTitle>
          <AnimationContainer>
            <GlowBox>Hover Me</GlowBox>
          </AnimationContainer>
          <CodeBlock>
            {`transition: all 0.3s ease;

&:hover {
