            {`position: relative;

&:before, &:after {
  content: '';
  position: absolute;
  width: 0;
  height: 0;
  opacity: 0;
  transition: width 0.3s, height 0.3s, opacity 0.3s;
}

&:before {
  top: 0;
  left: 0;
  border-top: 2px solid #3498db;
  border-left: 2px solid #3498db;
}

&:after {
  bottom: 0;
  right: 0;
  border-bottom: 2px solid #3498db;
  border-right: 2px solid #3498db;
}

&:hover:before, &:hover:after {
  width: 100%;
  height: 100%;
  opacity: 1;
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 색상 그라데이션 */}
        <AnimationCard>
          <AnimationTitle>색상 그라데이션 (Gradient)</AnimationTitle>
