            <GradientBox>Hover Me</GradientBox>
          </AnimationContainer>
          <CodeBlock>
            {`background: linear-gradient(to right, #3498db, #9b59b6);
background-size: 200% 100%;
background-position: 100% 0;
transition: background-position 0.5s ease;

&:hover {
  background-position: 0 0;
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 부드러운 진동 효과 */}
        <AnimationCard>
          <AnimationTitle>부드러운 진동 (Pulse)</AnimationTitle>
          <AnimationContainer>
            <PulseBox>Hover Me</PulseBox>
          </AnimationContainer>
          <CodeBlock>
            {`@keyframes pulse {
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
}

&:hover {
  animation: pulse 1s infinite;
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 텍스트 하이라이트 효과 */}
        <AnimationCard>
          <AnimationTitle>텍스트 하이라이트 (Highlight)</AnimationTitle>
          <AnimationContainer>
            <HighlightBox>Hover Me</HighlightBox>
          </AnimationContainer>
          <CodeBlock>
            {`position: relative;
z-index: 1;

&:before {
  content: '';
  position: absolute;
  z-index: -1;
  top: 0;
  bottom: 0;
  left: -2px;
  right: -2px;
  background-color: #fff566;
  transform: scaleY(0.1);
  transform-origin: bottom;
  transition: transform 0.3s ease;
}

&:hover:before {
  transform: scaleY(1);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 3D 푸시 효과 */}
        <AnimationCard>
          <AnimationTitle>3D 푸시 효과 (3D Push)</AnimationTitle>
          <AnimationContainer>
            <Push3DBox>Hover Me</Push3DBox>
          </AnimationContainer>
          <CodeBlock>
            {`transition: transform 0.3s ease;

&:hover {
  transform: perspective(500px) translateZ(30px);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 마우스 추적 효과 */}
        <AnimationCard>
          <AnimationTitle>마우스 추적 (Mouse Follow)</AnimationTitle>
          <AnimationContainer>
            <MouseFollowBox ref={mouseFollowBoxRef}>
              <MouseFollowSpot ref={mouseFollowSpotRef} />
              <MouseFollowText>Hover & Move</MouseFollowText>
            </MouseFollowBox>
          </AnimationContainer>
          <CodeBlock>
            {`position: relative;
overflow: hidden;

.spot {
  position: absolute;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: radial-gradient(circle, rgba(52,152,219,0.6) 0%, rgba(52,152,219,0) 70%);
  pointer-events: none;
  transform: translate(-50%, -50%);
  opacity: 0;
  transition: opacity 0.3s ease;
}

&:hover .spot {
  opacity: 1;
}

/* JavaScript 코드로 마우스 위치에 따라 .spot 요소의 위치를 실시간으로 업데이트합니다 */
/* 
const box = document.querySelector('.mouse-follow-box');
const spot = document.querySelector('.spot');

box.addEventListener('mousemove', (e) => {
  const rect = box.getBoundingClientRect();
  const x = e.clientX - rect.left;
  const y = e.clientY - rect.top;
  
  spot.style.left = x + 'px';
  spot.style.top = y + 'px';
});
*/`}
          </CodeBlock>
        </AnimationCard>
      </AnimationGrid>
    </Container>
  );
};

export default Hover;

