import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const TransEffect = () => {
  return (
    <Container>
      <Header>
        <BackLink to="/css-animations">
          <img src="public/images/copyCode.svg" alt="Copy Code Icon" />
        </BackLink>
        <Title>변형 효과</Title>
        <Subtitle>
          회전, 크기 조절, 기울임 등 요소의 형태를 변경하는 다양한 변형 효과
        </Subtitle>
      </Header>

      <AnimationGrid>
        {/* 기본 변형 효과 섹션 */}
        <SectionTitle>기본 변형 효과 (Basic Transforms)</SectionTitle>

        {/* 회전 변형 */}
        <AnimationCard>
          <AnimationTitle>회전 (Rotate)</AnimationTitle>
          <RotateBox />
          <CodeBlock>
            {`transform: rotate(0deg);
transition: transform 0.3s ease;
&:hover {
  transform: rotate(45deg);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 확대/축소 변형 */}
        <AnimationCard>
          <AnimationTitle>확대/축소 (Scale)</AnimationTitle>
          <ScaleBox />
          <CodeBlock>
            {`transform: scale(1);
transition: transform 0.3s ease;
&:hover {
  transform: scale(1.2);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 이동 변형 */}
        <AnimationCard>
          <AnimationTitle>이동 (Translate)</AnimationTitle>
          <TranslateBox />
          <CodeBlock>
            {`transform: translateY(0);
transition: transform 0.3s ease;
&:hover {
  transform: translateY(-20px);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 기울임 변형 */}
        <AnimationCard>
          <AnimationTitle>기울임 (Skew)</AnimationTitle>
          <SkewBox />
          <CodeBlock>
            {`transform: skew(0deg);
transition: transform 0.3s ease;
&:hover {
  transform: skew(15deg);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 원근감 변형 */}
        <AnimationCard>
          <AnimationTitle>원근감 (Perspective)</AnimationTitle>
          <PerspectiveContainer>
            <PerspectiveBox />
          </PerspectiveContainer>
          <CodeBlock>
            {`perspective: 500px;
.box {
  transition: transform 0.3s ease;
  transform: rotateY(0deg);
  &:hover {
    transform: rotateY(45deg);
  }
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 3D 플립 효과 - 추가 */}
        <AnimationCard>
          <AnimationTitle>3D 플립 (Flip)</AnimationTitle>
          <FlipContainer>
            <FlipBox>
              <FlipFront>앞면</FlipFront>
              <FlipBack>뒷면</FlipBack>
            </FlipBox>
          </FlipContainer>
          <CodeBlock>
            {`perspective: 1000px;
.flip-box {
  position: relative;
  transform-style: preserve-3d;
  transition: transform 0.6s;
  &:hover {
    transform: rotateY(180deg);
  }
}
.front, .back {
  position: absolute;
  backface-visibility: hidden;
}
.back {
  transform: rotateY(180deg);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 다중 변형 */}
        <AnimationCard>
          <AnimationTitle>다중 변형 (Multiple)</AnimationTitle>
          <MultiTransformBox />
          <CodeBlock>
            {`transform: scale(1) rotate(0deg);
transition: transform 0.3s ease;
&:hover {
  transform: scale(1.2) rotate(15deg) translateX(10px);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 반사 효과 */}
        <AnimationCard>
          <AnimationTitle>반사 효과 (Reflection)</AnimationTitle>
          <ReflectionContainer>
            <ReflectionBox />
          </ReflectionContainer>
          <CodeBlock>
            {`transform-style: preserve-3d;
transition: transform 0.4s ease;
&::after {
  content: '';
  position: absolute;
  width: 100%;
  height: 30px;
  bottom: -30px;
  background: linear-gradient(to bottom, rgba(52, 211, 153, 0.6), transparent);
  transform: rotateX(90deg);
  transform-origin: bottom;
  opacity: 0.3;
}
&:hover {
  transform: rotateX(-20deg);
}
&:hover::after {
  opacity: 0.8;
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 물결 효과 */}
        <AnimationCard>
          <AnimationTitle>물결 효과 (Wave)</AnimationTitle>
          <WaveBox />
          <CodeBlock>
            {`transition: border-radius 0.4s ease-in-out;
&:hover {
  animation: wave 1s ease-in-out infinite;
}
@keyframes wave {
  0% { border-radius: 8px; }
  25% { border-radius: 25px 8px 25px 8px; }
  50% { border-radius: 8px 25px 8px 25px; }
  75% { border-radius: 25px 8px 25px 8px; }
  100% { border-radius: 8px; }
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 트랜지션 효과 섹션 */}
        <SectionTitle>트랜지션 효과 (Transitions)</SectionTitle>

        {/* 색상 변경 트랜지션 */}
        <AnimationCard>
          <AnimationTitle>색상 변경</AnimationTitle>
          <ColorTransitionBox />
          <CodeBlock>
            {`transition: background-color 0.5s ease;
&:hover {
  background-color: #ff6b6b;
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 크기 변경 트랜지션 */}
        <AnimationCard>
          <AnimationTitle>크기 변경</AnimationTitle>
          <ScaleTransitionBox />
          <CodeBlock>
            {`transition: transform 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
&:hover {
  transform: scale(1.15);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 그림자 효과 트랜지션 */}
        <AnimationCard>
          <AnimationTitle>그림자 효과</AnimationTitle>
          <ShadowTransitionBox />
          <CodeBlock>
            {`transition: box-shadow 0.3s ease;
&:hover {
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 테두리 트랜지션 */}
        <AnimationCard>
          <AnimationTitle>테두리 변화</AnimationTitle>
          <BorderTransitionBox />
          <CodeBlock>
            {`transition: border-radius 0.4s ease;
&:hover {
  border-radius: 50%;
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 투명도 트랜지션 */}
        <AnimationCard>
          <AnimationTitle>투명도 변화</AnimationTitle>
          <OpacityTransitionBox />
          <CodeBlock>
            {`transition: opacity 0.3s ease;
&:hover {
  opacity: 0.5;
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 필터 효과 - 추가 */}
        <AnimationCard>
          <AnimationTitle>필터 효과 (Filter)</AnimationTitle>
          <FilterBox />
          <CodeBlock>
            {`transition: filter 0.4s ease;
&:hover {
  filter: blur(2px) brightness(1.2) contrast(1.4);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 그라데이션 변화 - 추가 */}
        <AnimationCard>
          <AnimationTitle>그라데이션 변화</AnimationTitle>
          <GradientBox />
          <CodeBlock>
            {`background: linear-gradient(45deg, #3b82f6, #8b5cf6);
background-size: 200% 200%;
background-position: 0% 0%;
transition: background-position 0.5s ease;
&:hover {
  background-position: 100% 100%;
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 다중 속성 트랜지션 */}
        <AnimationCard>
          <AnimationTitle>다중 속성 변화</AnimationTitle>
          <MultiTransitionBox />
          <CodeBlock>
            {`transition: all 0.5s ease;
&:hover {
  background-color: #4d8aff;
  transform: translateY(-10px);
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 텍스트 오버레이 트랜지션 */}
        <AnimationCard>
          <AnimationTitle>텍스트 오버레이 트랜지션</AnimationTitle>
          <TextOverlayContainer>
            <TextOverlayCard>
              <TextOverlayImage />
              <TextOverlay>오버레이 텍스트</TextOverlay>
            </TextOverlayCard>
          </TextOverlayContainer>
          <CodeBlock>
            {`position: relative;
overflow: hidden;

.image {
  transition: transform 0.4s ease;
}
.overlay {
  position: absolute;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.7);
  transform: translateY(100%);
  transition: transform 0.4s ease;
}
&:hover .image {
  transform: scale(1.1);
}
&:hover .overlay {
  transform: translateY(0);
}`}
          </CodeBlock>
        </AnimationCard>

        <SectionTitle>텍스트 효과 (Text Effects)</SectionTitle>

        {/* 텍스트 강조 효과 - 기존 */}
        <AnimationCard>
          <AnimationTitle>텍스트 강조 효과</AnimationTitle>
          <TextHighlightContainer>
            <TextHighlight>마우스를 올려보세요</TextHighlight>
          </TextHighlightContainer>
          <CodeBlock>
            {`position: relative;
&::after {
  content: '';
  position: absolute;
  width: 0;
  height: 3px;
  bottom: 0;
  left: 0;
  background-color: #ff6b6b;
  transition: width 0.3s ease;
}
&:hover::after {
  width: 100%;
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 텍스트 색상 변화 */}
        <AnimationCard>
          <AnimationTitle>텍스트 색상 변화</AnimationTitle>
          <TextColorTransition>색상이 변하는 텍스트</TextColorTransition>
          <CodeBlock>
            {`font-size: 1.2rem;
color: #333;
transition: color 0.3s ease;
&:hover {
  color: #ff6b6b;
  text-shadow: 0 0 8px rgba(255, 107, 107, 0.4);
}`}
          </CodeBlock>
        </AnimationCard>

        {/* 텍스트 크기 변화 */}
        <AnimationCard>
